      alert(`Detail produk: ${item.name}`)
    }

    return {
      leftDrawerOpen,
      toggleLeftDrawer,
      search,
      slide,
      slides,
      items,
      categories,
      selectedCategory,
      filterCategory,
      performSearch,
      filteredItems,
      viewDetail
    }
